hudson.plugins.warnings.GroovyParser.script:
import hudson.plugins.warnings.parser.Warning

String fileName = "unknown file"
int lineNumber = 0
if (matcher.group(3) && matcher.group(4)) {
  fileName = matcher.group(3)
  lineNumber = (matcher.group(4) as Integer) + 2
}

String category = matcher.group(1)
String message = matcher.group(2)

return new Warning(fileName, lineNumber, "Build Generator Error Parser", category, message);

import hudson.plugins.analysis.util.model.Priority
import hudson.plugins.warnings.parser.Warning

String messageType = matcher.group(1)
String fileName = matcher.group(2)
String lineNumber = matcher.group(3)
String scope = matcher.group(4)
String cmakeMessage = matcher.group(5)
String callStack = matcher.group(6)

String message = ""
if (cmakeMessage) {
     message += cmakeMessage + "<br />"
}
if (callStack) {
     message += "<pre>" + callStack + "</pre>"
}

Priority priority = Priority.NORMAL
if (messageType == "Deprecation Warning") {
     priority = Priority.LOW
} else if (messageType == "Error") {
     priority = Priority.HIGH
}
return new Warning(fileName, Integer.parseInt(lineNumber), "CMake", scope, message, priority);

import hudson.plugins.warnings.parser.Warning
String fileName = "file" int lineNumber = 1 String nature = matcher.group(1) String name = matcher.group(2)
return new Warning(fileName, lineNumber, "Build Generator Dependency Parser", "DEPENDENCY-" + nature, "No provider for ${nature} ${name}");
