import edu.hm.hafner.analysis.Severity

String fileName = matcher.group(1)
int lineNumber = 1
String form = matcher.group(2)
String category = matcher.group(3)
String message = ""
matcher.group(4).eachLine{ message += it.substring(4) + '\n' }
Severity severity = Severity.WARNING_NORMAL
if (message.contains("undefined") || category.equals("ERROR")) {
  severity = Severity.WARNING_HIGH
} else if (category.equals("STYLE-WARNING")) {
  severity = Severity.WARNING_LOW
}

builder.setFileName(fileName)
       .setLineStart(lineNumber)
       .setSeverity(severity)
       .setType( "SBCL Warning")
       .setCategory(category)
       .setMessage("In " + form + ": " + message)

return builder.buildOptional();
